package mystring;

public class indexAisPresentAtwhich {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="payilagam";
		
		for(int i=0;i<s.length();i++)
		{
			if(s.charAt(i)==('a'))
			{
				System.out.println(i);
			}
			
		}
		


	}

}
