package patterns;

public class program9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5; 
		for(int i=5;i>0;i--)
		{
			for(int j=i;j<=n;j++)
			{
			System.out.print(j%2+" ");
				}
				
			System.out.println();
			}
	
		}
	
	}

	


